INSERT INTO `mdl_cache_text` (`id`, `md5key`, `formattedtext`, `timemodified`) VALUES
(73, 'b9c0a1a9b896889da03022c53bf9019c', 'Descripcion', 1373273403),
(74, '60bda09b6a2d8048e8d15470fca626eb', '<span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">El </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">árabe</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, también llamado </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">arábigo</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">,</span><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">arabía</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">,</span><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> o </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">algarabía</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">,</span><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> (en árabe: العربية</span><i style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">al-ʻarabīyah</i><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> o عربي/عربى </span><i style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">ʻarabī</i><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, pronunciación: </span><a href="https://es.wikipedia.org/wiki/Alfabeto_fon%C3%A9tico_internacional" title="Alfabeto fonético internacional" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">[alʕaraˈbijja]</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> o </span><a href="https://es.wikipedia.org/wiki/Alfabeto_fon%C3%A9tico_internacional" title="Alfabeto fonético internacional" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">[ˈʕarabiː]</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">) es una </span><a href="https://es.wikipedia.org/wiki/Macrolengua" title="Macrolengua" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">macrolengua</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> de la </span><a href="https://es.wikipedia.org/wiki/Lenguas_sem%C3%ADticas" title="Lenguas semíticas" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">familia semítica</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, como el </span><a href="https://es.wikipedia.org/wiki/Idioma_arameo" title="Idioma arameo" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">arameo</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, el </span><a href="https://es.wikipedia.org/wiki/Idioma_hebreo" title="Idioma hebreo" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">hebreo</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, el </span><a href="https://es.wikipedia.org/wiki/Idioma_acadio" title="Idioma acadio" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">acadio</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, el</span><a href="https://es.wikipedia.org/wiki/Idioma_malt%C3%A9s" title="Idioma maltés" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">maltés</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> y otras lenguas similares. Es el octavo idioma más hablado en el mundo (número de hablantes nativos) y es la única lengua oficial en veinte países y complementaria en al menos otros seis, y una de las seis lenguas oficiales de la </span><a href="https://es.wikipedia.org/wiki/ONU" title="ONU" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">Organización de Naciones Unidas</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">. El </span><a href="https://es.wikipedia.org/wiki/%C3%81rabe_cl%C3%A1sico" title="Árabe clásico" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">árabe clásico</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> es también la </span><a href="https://es.wikipedia.org/wiki/Lengua_lit%C3%BArgica" title="Lengua litúrgica" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">lengua litúrgica</a> <span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">del </span><a href="https://es.wikipedia.org/wiki/Islam" title="Islam" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">islam</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">.</span>', 1375492068),
