(4, 1, 99, '', 'Arabe', 'Arabe básico', 'A100', '<span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">El </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">árabe</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, también llamado </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">arábigo</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">,</span><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">arabía</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">,</span><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> o </span><b style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">algarabía</b><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">,</span><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> (en árabe: العربية</span><i style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">al-ʻarabīyah</i><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> o عربي/عربى </span><i style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">ʻarabī</i><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, pronunciación: </span><a href="https://es.wikipedia.org/wiki/Alfabeto_fon%C3%A9tico_internacional" title="Alfabeto fonético internacional" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">[alʕaraˈbijja]</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> o </span><a href="https://es.wikipedia.org/wiki/Alfabeto_fon%C3%A9tico_internacional" title="Alfabeto fonético internacional" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">[ˈʕarabiː]</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">) es una </span><a href="https://es.wikipedia.org/wiki/Macrolengua" title="Macrolengua" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">macrolengua</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> de la </span><a href="https://es.wikipedia.org/wiki/Lenguas_sem%C3%ADticas" title="Lenguas semíticas" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">familia semítica</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, como el </span><a href="https://es.wikipedia.org/wiki/Idioma_arameo" title="Idioma arameo" class="mw-redirect" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">arameo</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, el </span><a href="https://es.wikipedia.org/wiki/Idioma_hebreo" title="Idioma hebreo" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">hebreo</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, el </span><a href="https://es.wikipedia.org/wiki/Idioma_acadio" title="Idioma acadio" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">acadio</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">, el</span><a href="https://es.wikipedia.org/wiki/Idioma_malt%C3%A9s" title="Idioma maltés" style="text-decoration: none; color: rgb(11, 0, 128); background-image: none; font-family: sans-serif; font-size: 13px; line-height: 19.1875px;">maltés</a><span style="font-family: sans-serif; font-size: 13px; line-height: 19.1875px;"> y otras lenguas similares.</span>', 'weeks', 1, 'a:13:{i:15;O:8:"stdClass":10:{s:2:"id";s:1:"4";s:2:"cm";i:15;s:3:"mod";s:5:"forum";s:7:"section";s:1:"0";s:7:"visible";s:1:"1";s:9:"groupmode";s:1:"0";s:10:"groupingid";s:1:"0";s:16:"groupmembersonly";s:1:"0";s:5:"extra";s:0:"";s:4:"name";s:10:"News+forum";}i:16;O:8:"stdClass":11:{s:2:"id";s:2:"12";s:2:"cm";i:16;s:3:"mod";s:8:"resource";s:7:"section";s:1:"0";s:7:"visible";s:1:"1";s:9:"groupmode";s:1:"0";s:10:"groupingid";s:1:"0";s:16:"groupmembersonly";s:1:"0";s:5:"extra";s:0:"";s:4:"icon";s:10:"f/html.gif";s:4:"name";s:13:"Generalidades";}i:17;O:8:"stdClass":11:{s:2:"id";s:2:"13";s:2:"cm";i:17;s:3:"mod";s:8:"resource";s:7:"section";s:1:"1";s:7:"visible";s:1:"1";s:9:"groupmode";s:1:"0";s:10:"groupingid";s:1:"0";s:16:"groupmembersonly";s:1:"0";s:5:"extra";s:0:"";s:4:"icon";s:10:"f/html.gif";s:4:"name";s:24:"Lecci%C3%B3n+1.+Alfabeto";}i:18;O:8:"stdClass":11:{s:2:"id";s:2:"14";s:2:"cm";i:18;s:3:"mod";s:8:"resource";s:7:"section";s:1:"2";s:7:"visible";s:1:"1";s:9:"groupmode";s:1:"0";s:10:"groupingid";s:1:"0";s:16:"groupmembersonly";s:1:"0";s:5:"extra";s:0:"";s:4:"icon";s:10:"f/html.gif";s:4:"name";s:35:"Lecci%C3%B3n+2.+Fonemas+Segmentales";}i:19;O:8:"stdClass":11:{s:2:"id";s:2:"15";s:2:"cm";i:19;s:3:"mod";s:8:"resource";s:7:"section";s:1:"3";s:7:"visible";s:1:"1";s:9:"groupmode";s:1:"0";s:10:"groupingid";s:1:"0";s:16:"groupmembersonly";s:1:"0";s:5:"extra";s:0:"";s:4:"icon";s:10:"f/html.gif";s:4:"name";s:40:"Lecci%C3%B3n+3.+Fonemas+suprasegmentales";}i:20;O:8:"stdClass":11:{s:2:"id";s:2:"16";s:2:"cm";i:20;s:3:"mod";s:8:"resource";s:7:"section";s:1:"4";s:7:"visible";s:1:"1";s:9:"groupmode";s:1:"0";s:10:"groupingid";s:1:"0";s:16:"groupmemb